mapper.select:

select 
	board_idx
	,title
	,writer
	,content
	,reg_date
from com_board
where gubun=#{gubun}




AND title LIKE CONCAT('%', #{keyword}, '%') AND 	
			reg_date between 
			#{start} 
			AND 
			#{now_date} 
			order by reg_date desc
	limit #{pageStart}
		 ,#{perPageNum}




order by reg_date desc, board_idx desc
limit  	 #{pageStart}
		 ,#{perPageNum}

select count(*) as totalCount from(select board_idx ,title ,writer ,content ,reg_date from com_board where gubun=#{gubun}
AND title like CONCAT('%', #{keyword}, '%') AND reg_date between #{start} AND #{now_date}) com_board
) com_board

