mapper.insert:


insert ignore 
	into 
		fail_list(
				question
				,answer
				,user_id
				,category
				)
		values(
				#{question}
				,#{answer}
				,#{user_id}
				,#{category}
			  )

mapper.delete:

delete 
	from fail_list




where 
	question=#{question}
	AND
	user_id=#{user_id}
	AND 
	category=#{category}

choose.when:

