from
	ans
where 
	user_id=#{user_id}
	AND category=#{category}

mapper.insert:
insert into quiz ( category, question, answer, user_id ) values
( #{item.category}, #{item.question}, #{item.answer}, #{item.user_id} )
on duplicate key update category = values(category), question = values(question), answer = values(answer), user_id = values(user_id);
